import React, { Component } from 'react';

class checkbox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fruits: [
                { id: 1, value: 'apple', isChecked: false },
                { id: 2, value: 'banana', isChecked: false },
                { id: 3, value: 'mango', isChecked: false },
                { id: 4, value: 'papaya', isChecked: false },
                { id: 5, value: 'kiwi', isChecked: false },
                { id: 6, value: 'pineapple', isChecked: false },
                { id: 7, value: 'strawberry', isChecked: false },
                { id: 8, value: 'vhgv', isChecked: false },
                { id: 9, value: 'orange', isChecked: false },
                { id: 10, value: 'peach', isChecked: false },
                { id: 11, value: 'melon', isChecked: false }
            ],
            allChecked: false,
            selectedValue: [],
        }
    }

    handleAllChecked = (event) => {

        let itemName = event.target.value;
        let isCheckedItem = event.target.checked;
        this.setState(prevState =>{
            let {fruits, allChecked, selectedValue} = prevState;
            if(itemName == "selectAll"){
                allChecked = isCheckedItem;
                if(allChecked === true ){
                    fruits = fruits.map((item,index)=>{
                        return{...item,isChecked : true};
                    })
                }
                else{
                    fruits = fruita.map((item,index)=>{
                        return{...item,isChecked: false};
                    })
                }

            }
            return {fruits, allChecked}
        })
    }


    render() {
        console.log("State", this.state);

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {this.state.fruits && this.state.fruits.length > 0 && this.state.fruits.map((fruit, index) => {
                    return (
                        <div>
                            <input
                                type="checkbox"
                                value={fruit.value}
                                checked={fruit.isChecked}
                                onChange={this.handleAllChecked} />
                            {fruit.value}
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
