import React, { Component } from 'react';
import Input from './share/input';


class form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Name: "Adfa",
            Email: "",
            Address: '',
            Zip : '',
            Message : ''

        }
    }
    handlerChange = (event) => {
        let itemvalue = event.target.value;
        let error = event.target.error;
        // let  
        this.setState(prevState => {
            let { value } = prevState;
            value = itemvalue
            return { value }
        })
    }
    render() {
        const {Name, Email, Address, Zip, Message} = this.state;
        return (
            <div>
                <Input
                    type="text"
                    labelTxt="Name"
                    error="Pls fill form"
                    onChange={this.handlerChange}
                    placholder="Your Name"
                    value={Name}
                />

            </div>
        );
    }
}

export default form;
