import React, {Component} from 'react';
import styled from 'styled-components';
import Gap from '../shared/gap';
import Heading from '../shared/heading';



const FromWrapper = styled.div`
    width: 600px;
    margin: auto;

    textarea{
        height: 100px;
        font-size: 18px;
        font-weight: 500;
        color: #222;
        resize: none;
        width: 100%;
        padding: 5px 10px;
        outline: none;
        border: 0px;
        border-bottom: 1px solid #ccc;
    }

    .send{
        font-size: 18px;
        height: 40px;
        line-height: 40px;
        font-weight: 500;
        text-align: center;
        width: 150px;
        outline: none;
        border: 1px solid #18bc9c;
        background-color: #18bc9c;
        color: #fff;
    }

`;

const Input = styled.input`
    width: 100%;
    padding: 5px 10px;
    height: 45px;
    line-height: 45px;
    font-size: 18px;
    font-weight: 500;
    color: #2222;
    border:0px;
    border-bottom: 1px solid #ccc;
    outline: none;

`;




class index extends Component {

    constructor(props){
        super(props);
        this.state = {

        }
    }




    changeHandler = e =>  {
        this.setState({
            [e.target.name] : e.target.value
        }, console.log(this.state))
    
    }
    

    render() {
        return(
            <div>
                <Heading title="contact Me" color="#000" />
                <Gap h="50px" />
                <FromWrapper>
                    <Input type="text" name="name" value={this.state.name} placeholder="Full Name" onChange={this.changeHandler}/>
                    <Gap h="10px" />
                    <Input type="text"  name="email" value={this.state.email} placeholder="Email" onChange={this.changeHandler}/>
                    <Gap h="10px" />
                    <textarea type="text" name="message" value={this.state.message} placeholder="Message" onChange={this.changeHandler}/>
                    <Gap h="10px" />
                    <button className="send">Send</button>
                </FromWrapper>

            </div>

        )
    }
}


export default index;
