        tempfruits.filter((fruits,index)=>{
            if(fruits.isChecked === true){
                dataobj.push(...tempfruits);
            }
        })
        console.log("obj",dataobj)
    }




    render() {
        const { fruits } = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
