import React, { Component } from 'react';
import styled from 'styled-components';

const Modal = styled.div`
    width: 100%;
    height: 100%;
    position: fixed;
    top: 0px;
    right: 0px;
    bottom:0px;
    left: 0px;
    margin: auto;
    display: block;
    background-color: rgba(0,0,0,0.61);
    z-index: 100;

`;

const Content = styled.div`
    width: ${props => props.width || '300px'};
    height: ${props => props.height || '300px'};
    position: fixed;
    top: 0px;
    right: 0px;
    bottom:0px;
    left: 0px;
    margin: auto;
    display: block;
    background-color:${props => props.bg || "#fff"};
    z-index: 101;
    overflow-y: auto;
    .close{
        text-align: right;
        font-size: 20px;
        color: #000;
        font-weight: 700;
        padding: 0px;
        cursor: pointer;
        border: 1px solid #000;
        width: 25px;
        height: 25px;
        border-radius: 50%;
        text-align: center;
        margin: 10px;
    }
`;

 class popUp extends Component {
     constructor(props){
         super(props);
         this.state = {

         }
     }

    render() {
        const {height, width, bg} = this.props;

        return (
            <div>
                <Content height={height} width={width} bg={bg}>
                    {this.props.children}
                </Content>
                <Modal />
            </div>
        )
    }
}

export default popUp;
