import React, { Component } from 'react';

class checkbox extends Component {
    constructor(props){
        super(props);
        this.state = {
            fruits : [
                {id:1,name:'apple',isChecked:false},
                {id:2,name:'banana',isChecked:false},
                {id:3,name:'mango',isChecked:false},
                {id:12,name:'guva',isChecked:false},
                {id:4,name:'papaya',isChecked:false},
                {id:5,name:'kiwi',isChecked:false},
                {id:6,name:'pineapple',isChecked:false},
                {id:7,name:'strawberry',isChecked:false},
                {id:8,name:'banana',isChecked:false},
                {id:9,name:'orange',isChecked:false},
                {id:10,name:'peach',isChecked:false},
                {id:11,name:'melon',isChecked:false}
            ],
        }
    }
    handleCheckChieldElement = (event) => {
        let fruites = this.state.fruits
        fruites.forEach(fruite => {
          if (fruite.name === event.target.name)
            fruite.isChecked =  event.target.checked
          })
        this.setState({fruits: fruites},()=>{
            console.log("item",this.state.fruits)
        })
     }


     handleAllChecked = (event) => {
        let fruites = this.state.fruits
        fruites.forEach(fruite => fruite.isChecked = event.target.checked) 
        this.setState({fruits: fruites},()=>{
            console.log("hey", this.state.fruits)
        })
      }


    render() {
        const {fruits} = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {fruits && fruits.length > 0 && fruits.map((fruit,index)=>{
                    return(
                       <div key={fruit.id} >
                            <input
                                type="checkbox" 
                                value={fruit.name} 
                                checked={fruit.isChecked}
                                onChange={this.handleCheckChieldElement} />
                                {fruit.name}
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
