import React, { Component } from 'react';

class checkbox extends Component {
    constructor(props){
        super(props);
        this.state = {
            fruits : [
                {id:1,name:'apple',isChecked:true},
                {id:2,name:'banana',isChecked:null},
                {id:3,name:'mango',isChecked:null},
                {id:3,name:'guva',isChecked:null},
                {id:4,name:'papaya',isChecked:null},
                {id:5,name:'kiwi',isChecked:null},
                {id:6,name:'pineapple',isChecked:null},
                {id:7,name:'strawberry',isChecked:null},
                {id:8,name:'banana',isChecked:null},
                {id:9,name:'orange',isChecked:null},
                {id:10,name:'peach',isChecked:null},
                {id:11,name:'melon',isChecked:null}
            ]
        }
    }
    handlerChange=(event)=>{
      console.log("hey",this.state)

    }
    render() {
        const {fruits} = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll" />
                <label>Select All</label>
                <hr></hr>
                {fruits && fruits.length > 0 && fruits.map((fruit,index)=>{
                    return(
                       <div>
                            <input
                                key={fruit.id} 
                                type="checkbox" 
                                value={fruit.name} 
                                checked={fruit.isChecked}
                                onChange={this.handlerChange} />
                            <label>{fruit.name}</label>
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
