import React, { Component } from 'react';

class checkbox extends Component {
    constructor(props){
        super(props);
        this.state = {
            fruits : [
                {id:1,name:'apple',isChecked:false},
                {id:2,name:'banana',isChecked:false},
                {id:3,name:'mango',isChecked:false},
                {id:12,name:'guva',isChecked:false},
                {id:4,name:'papaya',isChecked:false},
                {id:5,name:'kiwi',isChecked:false},
                {id:6,name:'pineapple',isChecked:false},
                {id:7,name:'strawberry',isChecked:false},
                {id:8,name:'banana',isChecked:false},
                {id:9,name:'orange',isChecked:false},
                {id:10,name:'peach',isChecked:false},
                {id:11,name:'melon',isChecked:false}
            ],
            selectAll : false
        }
    }
    handlerChange=(event)=>{
      console.log("hey",this.state)

    }
    handlerAllChecked=(event)=>{
        let tempfruits = this.state.fruits
        tempfruits.forEach((fruit)=>{
            if(fruit.isChecked = event.target.isChecked){
                this.setState({
                    fruits : tempfruits,
                    // selectAll = !this.state.selectAll
                })
            }
        })

    }
    render() {
        const {fruits} = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll"checked={this.state.selectAll} onChange={this.handlerAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {fruits && fruits.length > 0 && fruits.map((fruit,index)=>{
                    return(
                       <div key={fruit.id} >
                            <input
                                type="checkbox" 
                                value={fruit.name} 
                                checked={fruit.isChecked}
                                onChange={this.handlerChange} />
                            <label>{fruit.name}</label>
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
