import React, { Component } from 'react';
import styled from 'styled-components';
import Gap from '../shared/gap';
import Heading from '../shared/heading';



const FromWrapper = styled.div`
    width: 600px;
    margin: auto;
    text-align: left;

    textarea{
        width: 100%;
        padding: 6px 0px;
        height: 100px;
        font-size: 14px;
        font-weight: 500;
        color: #000;
        border: 0px;
        border-bottom: 1px solid #ccccccb5;
        outline: none;
        resize: none;
        font-weight: 600;
    }

    .send{
        font-size: 18px;
        height: 66px;
        line-height: 66px;
        font-weight: 500;
        text-align: center;
        width: 103px;
        outline: none;
        border: 1px solid #18bc9c;
        background-color: #18bc9c;
        color: #fff;
        border-radius: 4px;
        cursor: pointer;
        font-family: 'Asap', sans-serif;
        &:hover{
            opacity: 0.9;
        }
    }

`;

const Input = styled.input`
    width: 100%;
    padding: 6px 0px;
    height: 45px;
    line-height: 45px;
    font-size: 14px;
    font-weight: 500;
    color: #000;
    border: 0px;
    border-bottom: 1px solid #ccccccb5;
    outline: none;
    font-weight: 600;
`;




class index extends Component {

    constructor(props) {
        super(props);
        this.state = {

        }
    }




    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        }, console.log(this.state))

    }


    render() {
        return (
            <div>
                <Heading title="contact Me" color="#000" />
                <Gap h="50px" />
                <FromWrapper>
                    <Input type="text" name="name" value={this.state.name} placeholder="Full Name" onChange={this.changeHandler} />
                    <Gap h="10px" />
                    <Input type="text" name="email" value={this.state.email} placeholder="Email" onChange={this.changeHandler} />
                    <Gap h="10px" />
                    <textarea type="text" name="message" value={this.state.message} placeholder="Message" onChange={this.changeHandler} />
                    <Gap h="10px" />
                    <button className="send">Send</button>
                    <Gap h="100px" />
                </FromWrapper>

            </div>

        )
    }
}


export default index;
