import React, { Component } from 'react';

let dataobj = [];

class checkbox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fruits: [
                { id: 1, value: 'apple', isChecked: false },
                { id: 2, value: 'banana', isChecked: false },
                { id: 3, value: 'mango', isChecked: false },
                { id: 12, value: 'guva', isChecked: false },
                { id: 4, value: 'papaya', isChecked: false },
                { id: 5, value: 'kiwi', isChecked: false },
                { id: 6, value: 'pineapple', isChecked: false },
                { id: 7, value: 'strawberry', isChecked: false },
                { id: 8, value: 'banana', isChecked: false },
                { id: 9, value: 'orange', isChecked: false },
                { id: 10, value: 'peach', isChecked: false },
                { id: 11, value: 'melon', isChecked: false }
            ],

            allChecked:false
        }

    }

    // item

    handleCheckChieldElement=(event)=>{
        let fruites = this.state.fruits;
        fruites.filter((fruit)=>{
            if(fruit.value === event.target.value)
                fruit.isChecked = event.target.checked
            this.setState({
                fruits : fruites
            },()=>{
                console.log("Item", this.state.fruits)
            })

        })
    }

    // all
    handleAllChecked=(event)=>{

        let itemName = event.target.value;
        let isCheckedItem = event.target.checked;

        if(itemName==="selectAll"){
        let fruites = this.state.fruits;
        let allChecked = this.state.allChecked;
        allSelect = true;
        if(allChecked===true){
        fruites.filter(fruites =>fruites.isChecked = event.target.checked);
        for(let i=0;i<fruites.length;i++){
            dataobj.push({"id":fruites[i].value});
        }
        console.log("obj",dataobj)
        this.setState({fruits : fruites},()=>{
            console.log("all",this.state.fruits)
        });
    }
    else{
        dataobj=[];
    }

    }
    else{
        let fruites = this.state.fruits;
        fruites.filter((fruit)=>{
            if(fruit.value === event.target.value)
                fruit.isChecked = event.target.checked
            this.setState({
                fruits : fruites
            })

        })
    }

    }




    render() {
        const { fruits } = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {fruits && fruits.length > 0 && fruits.map((fruit, index) => {
                    return (
                        <div key={fruit.id} >
                            <input
                                type="checkbox"
                                value={fruit.value}
                                checked={fruit.isChecked}
                                onChange={this.handleAllChecked} />
                            {fruit.value}
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
