import React, { Component } from 'react';
import Input from './share/input';


class form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fieldObj: {
                Name: "",
                Email: "",
                Address: '',
                Zip: '',
                Message: ''

            },
            errorStyle: false
        }
    }


    handlerChange = key => event => {
        let val = event.target.value;
        const temp = Object.assign({}, this.state.fieldObj);

        temp[key] = event.target.value;

        this.setState({
            ...temp
        }, () => {
            if(val > 3){
               this.setState({
                    errorStyle : true
               })
            }
            else{
                this.setState({
                    errorStyle : false
               })
            }
        })



    }

    render() {
        const { Name, Email, Address, Zip, Message } = this.state.fieldObj;
        return (
            <div>
                <Input
                    type="text"
                    labelTxt="Name"
                    error="Pls fill form"
                    errorStyle={this.state.errorStyle}
                    onChange={this.handlerChange('Name')}
                    placholder="Your Name" 
                    defaultValue={Name}
                />
                <Input
                    type="text"
                    labelTxt="Email"
                    error="Pls fill form"
                    onChange={this.handlerChange('Email')}
                    placholder="Your Name"
                    defaultValue={Email}
                />

            </div>
        );
    }
}

export default form;
