import React, { Component } from 'react';
import Input from './share/input';


class form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Name: "Adfa",
            Email: "",
            Address: '',
            Zip: '',
            Message: ''

        }
    }

    render() {
        const { Name, Email, Address, Zip, Message } = this.state;
        return (
            <div>
                <Input
                    type="text"
                    labelTxt="Name"
                    error="Pls fill form"
                    onChange={this.handlerChange('Name')}
                    placholder="Your Name"
                    defaultValue={Name}
                />
                <Input
                    type="text"
                    labelTxt="Email"
                    error="Pls fill form"
                    onChange={this.handlerChange('Email')}
                    placholder="Your Name"
                    defaultValue={Email}
                />

            </div>
        );
    }
}

export default form;
