    console.log("window.pageYOffset", window.pageYOffset)
    return (
      <div className="App">
        <Header />
        <Banner />
        <Gap h="40px" />
        <Portfolio />
        <Gap h="40px" />
        <About />
        <Gap h="40px" />
        <Contact />
        <Gap h="40px" />
        <Footer />
        <BackToTop onClick={() => window.scrollTo = 0}><i className="fas fa-angle-up"></i></BackToTop>
      </div>
    );
  }
}
export default App;
