    //             return (data)
    //         });

    //         this.setState({
    //             fruits: [...temp]
    //         })
    //     }
    // };

    handleAllChecked = (event) =>{
        let itemName = event.target.value;
        let isCheckedItems = event.target.checked;
        this.setState(prevState  => {
            let { fruits, allChecked, selectedValue} = prevState;
            allChecked = isCheckedItems;
            if(itemName === "selectAll"){
                if(allChecked === true){
                    console.log(allChecked);
                    fruits = fruits.map(item => {
                        selectedValue.push(item.isChecked);
                        return {...item, isChecked: true}
                    }) 
                    console.log("data", selectedValue)
                }

                else{
                    fruits = fruits.map(item => {
                        // const index = selectedValue.indexOf(item.id);
                        // selectedValplice(index, 1);
                         selectedValue = [];
                        // selectedValue.splice(index, 1);
                        return {...item, isChecked : false}
                    }) 
                    console.log(selectedValue);
                }
            }
            else{
                let tempFruits = fruits;
                tempFruits.forEach(item => {
                    if (item.value === itemName) {
                        item.isChecked = isCheckedItems;
                        console.log("Hey",item.isChecked, fruits)
                    }
                });
            }

            return {fruits , allChecked}
        })
    }


    // for(let i = 0; i < fruits.length; i++){
    //     if(itemName === fruits[i].value){
    //         fruits[i].isChecked = true;console.log("hey", fruits[i] )
    //     }
    //     else{
    //         fruits[i].isChecked = false;
    //     }
    // }

    render() {
        console.log("State", this.state);

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {this.state.fruits && this.state.fruits.length > 0 && this.state.fruits.map((fruit, index) => {
                    return (
                        <div>
                            <input
                                type="checkbox"
                                value={fruit.value}
                                checked={fruit.isChecked}
                                onChange={this.handleAllChecked} />
                                {fruit.value}
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
