import React, { Component } from 'react';

let dataobj = [];

class checkbox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fruits: [
                { id: 1, value: 'apple', isChecked: false },
                { id: 2, value: 'banana', isChecked: false },
                { id: 3, value: 'mango', isChecked: false },
                { id: 12, value: 'guva', isChecked: false },
                { id: 4, value: 'papaya', isChecked: false },
                { id: 5, value: 'kiwi', isChecked: false },
                { id: 6, value: 'pineapple', isChecked: false },
                { id: 7, value: 'strawberry', isChecked: false },
                { id: 8, value: 'banana', isChecked: false },
                { id: 9, value: 'orange', isChecked: false },
                { id: 10, value: 'peach', isChecked: false },
                { id: 11, value: 'melon', isChecked: false }
            ],
            allChecked: false,
        }
    }

    handleAllChecked = (event) =>{
        let itemName = event.target.value;
        let isCheckedItems = event.target.checked;
        this.setState(prevState  => {
            let { fruits, allChecked} = prevState;
            allChecked = isCheckedItems;
            if(itemName === "selectAll"){
                if(allChecked === true){
                    console.log(allChecked);
                    fruits = fruits.map(item => ({...item, isChecked: true}))
                }
                else{
                    fruits = fruits.map((item) => ({...item, isChecked : false}))
                    console.log(allChecked);
                }
            }
        // <------------------------->//
            else{
                let tempFruits = fruits;
                tempFruits.forEach(item => {
                    if (item.value === itemName) {
                        tempFruits.isCheckedItems = true
                    }

                });

            }


            return {fruits , allChecked}

        })
    }


    // for(let i = 0; i < fruits.length; i++){
    //     if(itemName === fruits[i].value){
    //         fruits[i].isChecked = true;console.log("hey", fruits[i] )
    //     }
    //     else{
    //         fruits[i].isChecked = false;
    //     }
    // }

    render() {
        const { fruits } = this.state;

        return (
            <div>
                <input type="checkbox" value="selectAll" onClick={this.handleAllChecked} />
                <label>Select All</label>
                <hr></hr>
                {fruits && fruits.length > 0 && fruits.map((fruit, index) => {
                    return (
                        <div key={fruit.id} >
                            <input
                                type="checkbox"
                                value={fruit.value}
                                checked={fruit.isChecked}
                                onChange={this.handleAllChecked} />
                            {fruit.value}
                        </div>
                    )
                })
                }
            </div>
        );
    }
}

export default checkbox;
