import React, { Component } from 'react'
import styled from 'styled-components';
import PopUp from '../shared/popUp';

const Card = styled.div`
    width: 100%;
    height: 260px;
    position: relative;
    overflow: hidden;
    .hover_card{
        width: 100%;
        height: 100%;
        position: absolute;
        top: ${props => props.hover ? "0px" : "-100%"};
        bottom: 0px;
        right: 0px;
        left: 0px;
        transition: all 0.1s;
        background-color: #18bc9c;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 30px;
        color: #fff;
    }
    img{
        max-width: 100%;
    }

    .close{
        text-align: right;
        font-size: 20px;
        color: #000;
        font-weight: 700;
        padding: 10px 0px;
    }
    
`;

class portfolioCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hover: false,
            showPopup: false


        }
    }
    render() {
        const { logo, title, bigImage, url, description, skill } = this.props.item;
        console.log("logo", logo)

        return (
            <div>
            <Card
                hover={this.state.hover}
                onMouseOut={() => this.setState({ hover: false })}
                onMouseOver={() => this.setState({ hover: true })}
            >
                <img src={logo} />
                <div className="hover_card" onClick={() => this.setState({showPopup : true})}>+</div>
            </Card>
           {this.state.showPopup &&
            <PopUp>
                <div className="close" onClick={() => this.setState({showPopup: false})}>X</div>
            <div style={{padding:'10px'}}>
                show me please
            </div>
        </PopUp>}
         </div>
        )
        
       
    }
}


export default portfolioCard;
